#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <rds/encoder.h>
// pydoc.h is automatically generated in the build directory
#include <encoder_pydoc.h>

void bind_encoder(py::module& m)
{

    using encoder    = ::gr::rds::encoder;


    py::class_<encoder, gr::sync_block, gr::block, gr::basic_block,
        std::shared_ptr<encoder>>(m, "encoder", D(encoder))

        .def(py::init(&encoder::make),
           py::arg("pty_locale"),
           py::arg("pty"),
           py::arg("ms"),
           py::arg("mono_stereo"),
           py::arg("artificial_head"),
           py::arg("compressed"),
           py::arg("static_pty"),
           py::arg("ps"),
           py::arg("af1"),
           py::arg("tp"),
           py::arg("ta"),
           py::arg("pi_country_code"),
           py::arg("pi_coverage_area"),
           py::arg("pi_reference_number"),
           py::arg("extended_country_code"),
           py::arg("radiotext"),
           py::arg("log"),
           py::arg("debug"),
           D(encoder,make)
        )
        




        
        .def("update_ps",&encoder::update_ps,
            py::arg("ps"),
            D(encoder,update_ps)
        )

        ;




}








